<?phpclass ChangeCategoryTemplate {    public static $checkCategory = false;    private $redisignedCategories = 2;    private $template = 'wiki';	private $Register;	public function __construct($params = array()) {		$this->Register = Register::getInstance();	}			public function common($params, $hookName) {        if ($hookName === 'view_category') {            if (is_object($params) && $params instanceof NewsSectionsEntity) {                $path = $params->getPath();                if ($params->getId() == $this->redisignedCategories ||                    preg_match('#\.'.preg_quote($this->redisignedCategories).'\.#', '.' . $path)) {                    self::$checkCategory = true;                    Config::$settings['template'] = $this->template;                }            }        }        return $params;	}}